package com.krmu.Assignment1;

public class Account {
    private int accountNumber;
    private String accountHolderName;
    private double balance;
    private String email;
    private String phoneNumber;

    public Account(int accountNumber, String accountHolderName, double initialBalance, String email, String phoneNumber) {
        this.accountNumber = accountNumber;
        this.accountHolderName = accountHolderName != null ? accountHolderName.trim() : "";
        this.balance = Math.max(0, initialBalance);
        this.email = email != null ? email.trim() : "";
        this.phoneNumber = phoneNumber != null ? phoneNumber.trim() : "";
    }

    public int getAccountNumber() {
        return accountNumber;
    }

    public String getAccountHolderName() {
        return accountHolderName;
    }

    public double getBalance() {
        return balance;
    }

    public String getEmail() {
        return email;
    }

    public String getPhoneNumber() {
        return phoneNumber;
    }

    public boolean deposit(double amount) {
        if (amount <= 0) { System.out.println("Amount must be +ve"); return false; }
        balance += amount;
        return true;
    }

    public boolean withdraw(double amount) {
        if (amount <= 0) { System.out.println("Amount must be +ve"); return false; }
        if (amount > balance) { System.out.println("Not enough balance"); return false; }
        balance -= amount;
        return true;
    }

    public void displayAccountDetails() {
        System.out.println("Acc No: " + accountNumber);
        System.out.println("Name  : " + accountHolderName);
        System.out.println("Email : " + email);
        System.out.println("Phone : " + phoneNumber);
        System.out.println("Bal   : " + balance);
    }

    public void updateContactDetails(String email, String phoneNumber) {
        if (email != null) this.email = email.trim();
        if (phoneNumber != null) this.phoneNumber = phoneNumber.trim();
    }
}

package com.krmu.Assignment1;

public class Account {
    private int accountNumber;
    private String accountHolderName;
    private double balance;
    private String email;
    private String phoneNumber;

    public Account(int accountNumber, String accountHolderName, double initialBalance, String email, String phoneNumber) {
        this.accountNumber = accountNumber;
        this.accountHolderName = accountHolderName != null ? accountHolderName.trim() : "";
        this.balance = Math.max(0, initialBalance);
        this.email = email != null ? email.trim() : "";
        this.phoneNumber = phoneNumber != null ? phoneNumber.trim() : "";
    }

    public int getAccountNumber() {
        return accountNumber;
    }

    public boolean deposit(double amount) {
        if (amount <= 0) { System.out.println("Amount must be +ve"); return false; }
        balance += amount;
        return true;
    }

    public boolean withdraw(double amount) {
        if (amount <= 0) { System.out.println("Amount must be +ve"); return false; }
        if (amount > balance) { System.out.println("Not enough balance"); return false; }
        balance -= amount;
        return true;
    }

    public void displayAccountDetails() {
        System.out.println("Acc No: " + accountNumber);
        System.out.println("Name  : " + accountHolderName);
        System.out.println("Email : " + email);
        System.out.println("Phone : " + phoneNumber);
        System.out.println("Bal   : " + balance);
    }

    public void updateContactDetails(String email, String phoneNumber) {
        if (email != null) this.email = email.trim();
        if (phoneNumber != null) this.phoneNumber = phoneNumber.trim();
    }
}

package com.krmu.Assignment1;

import java.util.Scanner;

public class UserInterface {
    private static final int MAX_ACCOUNTS = 100;

    private Account[] accounts = new Account[MAX_ACCOUNTS];
    private int accountCount = 0;
    private Scanner sc = new Scanner(System.in);

    // Find account index by account number
    private int findAccountIndexByNumber(int accNo) {
        for (int i = 0; i < accountCount; i++) {
            if (accounts[i].getAccountNumber() == accNo) {
                return i;
            }
        }
        return -1;
    }

    public void createAccount() {
        if (accountCount >= MAX_ACCOUNTS) {
            System.out.println("Cannot create more accounts. Storage full.");
            return;
        }
        try {
            System.out.print("Enter account number (integer): ");
            int accNo = sc.nextInt();
            sc.nextLine(); // consume newline

            if (findAccountIndexByNumber(accNo) != -1) {
                System.out.println("Account number already exists. Try again.");
                return;
            }

            System.out.print("Enter account holder name: ");
            String name = sc.nextLine().trim();

            System.out.print("Enter initial balance: ");
            double initBal = sc.nextDouble();
            sc.nextLine();

            System.out.print("Enter email: ");
            String email = sc.nextLine().trim();

            System.out.print("Enter phone number: ");
            String phone = sc.nextLine().trim();

            accounts[accountCount] = new Account(accNo, name, initBal, email, phone);
            accountCount++;
            System.out.println("Account created successfully.");
        } catch (Exception e) {
            System.out.println("Invalid input. Please try again.");
            sc.nextLine(); // clear buffer if needed
        }
    }

    public void performDeposit() {
        try {
            System.out.print("Enter account number: ");
            int accNo = sc.nextInt();
            sc.nextLine();

            int idx = findAccountIndexByNumber(accNo);
            if (idx == -1) {
                System.out.println("Account not found.");
                return;
            }

            System.out.print("Enter amount to deposit: ");
            double amt = sc.nextDouble();
            sc.nextLine();

            accounts[idx].deposit(amt);
        } catch (Exception e) {
            System.out.println("Invalid input. Please try again.");
            sc.nextLine();
        }
    }

    public void performWithdrawal() {
        try {
            System.out.print("Enter account number: ");
            int accNo = sc.nextInt();
            sc.nextLine();

            int idx = findAccountIndexByNumber(accNo);
            if (idx == -1) {
                System.out.println("Account not found.");
                return;
            }

            System.out.print("Enter amount to withdraw: ");
            double amt = sc.nextDouble();
            sc.nextLine();

            accounts[idx].withdraw(amt);
        } catch (Exception e) {
            System.out.println("Invalid input. Please try again.");
            sc.nextLine();
        }
    }

    public void showAccountDetails() {
        try {
            System.out.print("Enter account number: ");
            int accNo = sc.nextInt();
            sc.nextLine();

            int idx = findAccountIndexByNumber(accNo);
            if (idx == -1) {
                System.out.println("Account not found.");
                return;
            }

            accounts[idx].displayAccountDetails();
        } catch (Exception e) {
            System.out.println("Invalid input. Please try again.");
            sc.nextLine();
        }
    }

    public void updateContact() {
        try {
            System.out.print("Enter account number: ");
            int accNo = sc.nextInt();
            sc.nextLine();

            int idx = findAccountIndexByNumber(accNo);
            if (idx == -1) {
                System.out.println("Account not found.");
                return;
            }

            System.out.print("Enter new email: ");
            String email = sc.nextLine().trim();
            System.out.print("Enter new phone number: ");
            String phone = sc.nextLine().trim();

            accounts[idx].updateContactDetails(email, phone);
        } catch (Exception e) {
            System.out.println("Invalid input. Please try again.");
            sc.nextLine();
        }
    }

    public void mainMenu() {
        int choice = -1;
        do {
            System.out.println();
            System.out.println("Welcome to the Banking Application!");
            System.out.println("1. Create a new account");
            System.out.println("2. Deposit money");
            System.out.println("3. Withdraw money");
            System.out.println("4. View account details");
            System.out.println("5. Update contact details");
            System.out.println("6. Exit");
            System.out.print("Enter your choice: ");

            try {
                choice = sc.nextInt();
                sc.nextLine();
            } catch (Exception e) {
                System.out.println("Please enter a valid number.");
                sc.nextLine();
                continue;
            }

            switch (choice) {
                case 1:
                    createAccount();
                    break;
                case 2:
                    performDeposit();
                    break;
                case 3:
                    performWithdrawal();
                    break;
                case 4:
                    showAccountDetails();
                    break;
                case 5:
                    updateContact();
                    break;
                case 6:
                    System.out.println("Exiting... Thank you!");
                    break;
                default:
                    System.out.println("Invalid choice. Try again.");
            }
        } while (choice != 6);
    }

    public static void main(String[] args) {
        UserInterface ui = new UserInterface();
        ui.mainMenu();
    }
}

package com.krmu.Assignment1;

import java.util.Scanner;

public class UserInterface {
    private Account[] accounts = new Account[100];
    private int count = 0;
    private Scanner sc = new Scanner(System.in);

    public void create() {
        System.out.print("Acc no: ");
        int no = sc.nextInt(); sc.nextLine();
        System.out.print("Name: ");
        String name = sc.nextLine();
        System.out.print("Balance: ");
        double bal = sc.nextDouble(); sc.nextLine();
        System.out.print("Email: ");
        String email = sc.nextLine();
        System.out.print("Phone: ");
        String phone = sc.nextLine();
        accounts[count++] = new Account(no, name, bal, email, phone);
        System.out.println("Created!");
    }

    public void deposit() {
        System.out.print("Acc no: ");
        int no = sc.nextInt(); sc.nextLine();
        Account a = find(no);
        if (a == null) { System.out.println("Not found"); return; }
        System.out.print("Amount: ");
        double amt = sc.nextDouble(); sc.nextLine();
        a.deposit(amt);
    }

    public void withdraw() {
        System.out.print("Acc no: ");
        int no = sc.nextInt(); sc.nextLine();
        Account a = find(no);
        if (a == null) { System.out.println("Not found"); return; }
        System.out.print("Amount: ");
        double amt = sc.nextDouble(); sc.nextLine();
        a.withdraw(amt);
    }

    public void view() {
        System.out.print("Acc no: ");
        int no = sc.nextInt(); sc.nextLine();
        Account a = find(no);
        if (a == null) { System.out.println("Not found"); return; }
        a.displayAccountDetails();
    }

    public void update() {
        System.out.print("Acc no: ");
        int no = sc.nextInt(); sc.nextLine();
        Account a = find(no);
        if (a == null) { System.out.println("Not found"); return; }
        System.out.print("Email: ");
        String email = sc.nextLine();
        System.out.print("Phone: ");
        String phone = sc.nextLine();
        a.updateContactDetails(email, phone);
    }

    private Account find(int no) {
        for (int i = 0; i < count; i++) {
            if (accounts[i].getAccountNumber() == no) return accounts[i];
        }
        return null;
    }
}
